<!DOCTYPE html>
<html lang="es">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>@yield('title', 'Libreria')</title>
    <link rel="shortcut icon" href="favicon.ico" type="image/x-icon">
    @vite(['resources/sass/app.scss', 'resources/js/app.js'])

    <style>

        .fruteria {
            flex-direction: column;
            width: 100vw;
            height: 100vh;
            background-color:rgb(255, 255, 255);
        }

        header, footer, .contentContainer {
            width: 100%;
        }

        header, footer {
            font-family: "Righteous", serif;
            text-transform: uppercase;
            background:rgb(231, 201, 211);
            color: white;
        }

        h1 {
            margin: 0px;
        }

        header {
            display: flex;
            justify-content: center;
            align-items: center;
            flex-direction: row;
            gap: 20px;
            height: 10%;
        }

        .contentContainer {
            display: flex;
            justify-content: center;
            align-items: center;
            height: 85%;
            width: 100%;
        }

        .contenedorTabla {
            display: flex;
            justify-content: center;
            align-items: center;
            flex-direction: column;
            width: 60%;
            height: 100%;
        }

        table {
            padding: 20px;
        }

        footer {
            height: 5%;
        }   

        p {
            margin: 0;
            padding: 0;
        }

        .btnContainer {
            display: flex;
        }

        .button {
            background: #A074C4;
            color: white;
        }

        .button:hover {
            background:rgb(134, 98, 163);
            color: white;
        }

        .bookContainer {
            display: grid;
            grid-template-columns: repeat(2, 1fr);
            justify-content: center;
            align-items: center;
        }

        .col1 {
            display: flex;
            justify-content: center;
            align-items: center;
        }

        .col2 {
            display: flex;
            height: 100%;
            width: 100%;
            flex-direction: column;
            gap: 30px;
        }

        .img {
            width: 25px;
        }

        .formulario {
            display: grid;
            border: 1px solid rgb(236, 196, 209);
            border-radius: 8px;
            padding: 41px;
            background-color: rgb(236, 213, 221);
            gap: 37px;
        }

        .loginForm {
            display: grid;
            border: 1px solid rgb(236, 196, 209);
            border-radius: 8px;
            padding: 50px 35px;
            background-color: rgb(236, 213, 221);
            gap: 37px;
        }

        .login {
            position: absolute;
            top: 33px;
            right: 33px;
            display: flex;
            justify-content: center;
            flex-direction: row;
            gap: 10px;
        }

        .svgContainer {
            width: 70px;
            height: auto;
        }

        .frutaContainer {
            display: flex;
            justify-content: center;
            align-items: center;
            flex-direction: column;
            background-color: rgb(236, 213, 221);
            padding: 20px;
            width: auto;
            border-radius: 10px;
            gap: 20px;
        }

        .descripcionFruta {
            display: flex;
            justify-content: center;
            align-items: center;
            flex-direction: column;
            gap: 5px;
        }

        .imgFruta {
            border: 1px solid black;
            width: 300px;
        }

        .descripcion {
            display: flex;
            justify-content: center;
            align-items: center;
            flex-direction: column;
            gap: 6px;
        }

        .contenedorShow {
            display: flex;
            justify-content: center;
            align-items: center; 
            flex-direction: column;
            gap: 30px;
        }

        .bienvenida {
            color: white;
            position: absolute;
            top: 35px;
            left: 35px;
        }

        .loginContainer {
            display: flex;
            justify-content: center;
            align-items: center; 
            flex-direction: column;
            gap: 30px;
        }

        .fondoImagen {
            background-color: white;
        }

    </style>
</head>
<body>
    <div class="fruteria d-flex justify-content-center align-items-center">
        <header class="d-flex justify-content-center align-items-center">
            <div class="svgContainer">
